index fa51f89d..57a6c4ba 100644
--- a/server/src/test/java/org/elasticsearch/common/TimeBasedUUIDGeneratorTests.java
+++ b/server/src/test/java/org/elasticsearch/common/TimeBasedUUIDGeneratorTests.java
@@ -47,12 +47,14 @@ public class TimeBasedUUIDGeneratorTests extends ESTestCase {

     public void testTimeBasedUUIDClockReset() {
         // Simulate a clock that resets itself after reaching a threshold.
+        // Use a safe starting time that's at least 1 day after epoch to avoid issues with small timestamps
+        Instant startTime = Instant.ofEpochMilli(Math.max(Instant.now().toEpochMilli(), ChronoUnit.DAYS.getDuration().toMillis()));
         final Supplier<Long> unreliableClock = new TestClockResetTimestampSupplier(
-            Instant.now(),
+            startTime,
             1,
             50,
             ChronoUnit.MILLIS,
-            Instant.now().plus(100, ChronoUnit.MILLIS)
+            startTime.plus(100, ChronoUnit.MILLIS)
         );
         final UUIDGenerator generator = createGenerator(unreliableClock, () -> 0, new TestRandomMacAddressSupplier());

@@ -101,8 +103,12 @@ public class TimeBasedUUIDGeneratorTests extends ESTestCase {
     }

     public void testUUIDEncodingDecodingWithRandomValues() {
+        // Use a reasonable timestamp range that avoids very small timestamps (< 1 day after epoch)
+        // to prevent issues with the UUID generator's lastTimestamp initialization to 0
+        Instant minInstant = Instant.ofEpochMilli(Math.max(Instant.now().toEpochMilli() - ChronoUnit.DAYS.getDuration().toMillis(), ChronoUnit.DAYS.getDuration().toMillis()));
+        Instant maxInstant = Instant.now();
         testUUIDEncodingDecodingHelper(
-            randomInstantBetween(Instant.now().minus(1, ChronoUnit.DAYS), Instant.now()).toEpochMilli(),
+            randomInstantBetween(minInstant, maxInstant).toEpochMilli(),
             randomIntBetween(0, 0x00FF_FFFF),
             new TestRandomMacAddressSupplier().get()
         );
