+    private Path copyKeystoreToTempDir(Path tempDir) {
+        String keystoreFileName = "signing." + (inFipsJvm() ? "bcfks" : "jks");
+        String resourcePath = "/org/elasticsearch/xpack/security/signature/" + keystoreFileName;
+        Path keystorePath = tempDir.resolve(keystoreFileName);
+        try (
+            InputStream is = CrossClusterApiKeySignerTests.class.getResourceAsStream(resourcePath);
+            OutputStream os = Files.newOutputStream(keystorePath)
+        ) {
+            if (is == null) {
+                throw new IllegalStateException("Cannot find resource: " + resourcePath);
+            }
+            byte[] buffer = new byte[4096];
+            int readBytes;
+            while ((readBytes = is.read(buffer)) > 0) {
+                os.write(buffer, 0, readBytes);
+            }
+        } catch (IOException e) {
+            throw new RuntimeException("Failed to copy keystore from resources", e);
+        }
+        return keystorePath;
     }
