 package org.elasticsearch.index.mapper.completion;

+import org.elasticsearch.ElasticSearchIllegalArgumentException;
 import org.elasticsearch.common.xcontent.XContentBuilder;
 import org.elasticsearch.common.xcontent.json.JsonXContent;
 import org.elasticsearch.index.mapper.DocumentMapper;
 import org.elasticsearch.index.mapper.FieldMapper;
 import org.elasticsearch.index.mapper.MapperTestUtils;
+import org.elasticsearch.index.mapper.ParseContext;
 import org.elasticsearch.index.mapper.core.CompletionFieldMapper;
 import org.elasticsearch.test.ElasticsearchTestCase;
 import org.junit.Test;
@@ -112,4 +114,46 @@ public class CompletionFieldMapperTests extends ElasticsearchTestCase {
         assertThat(configMap.get("analyzer").toString(), is("simple"));
     }

+    @Test(expected = ElasticSearchIllegalArgumentException.class)
+    public void testThatFloatWeightsAreRejected() throws Exception {
+        String mapping = jsonBuilder().startObject().startObject("type1")
+                .startObject("properties").startObject("completion")
+                .field("type", "completion")
+                .endObject().endObject()
+                .endObject().endObject().string();
+
+        DocumentMapper defaultMapper = MapperTestUtils.newParser().parse(mapping);
+
+        XContentBuilder builder = jsonBuilder()
+                .startObject()
+                .startObject("completion")
+                .field("input", "test suggestion")
+                .field("weight", 2.5)
+                .endObject()
+                .endObject();
+
+        defaultMapper.parse("type1", "1", builder.bytes());
+    }
+
+    @Test(expected = ElasticSearchIllegalArgumentException.class)
+    public void testThatDoubleWeightsAreRejected() throws Exception {
+        String mapping = jsonBuilder().startObject().startObject("type1")
+                .startObject("properties").startObject("completion")
+                .field("type", "completion")
+                .endObject().endObject()
+                .endObject().endObject().string();
+
+        DocumentMapper defaultMapper = MapperTestUtils.newParser().parse(mapping);
+
+        XContentBuilder builder = jsonBuilder()
+                .startObject()
+                .startObject("completion")
+                .field("input", "test suggestion")
+                .field("weight", 123.456)
+                .endObject()
+                .endObject();
+
+        defaultMapper.parse("type1", "1", builder.bytes());
+    }
+
 }
