diff --git a/src/test/java/org/elasticsearch/common/unit/ByteSizeValueTests.java b/src/test/java/org/elasticsearch/common/unit/ByteSizeValueTests.java
index 17d6437..3b325fc 100644
--- a/src/test/java/org/elasticsearch/common/unit/ByteSizeValueTests.java
+++ b/src/test/java/org/elasticsearch/common/unit/ByteSizeValueTests.java
@@ -53,4 +53,31 @@ public class ByteSizeValueTests extends ElasticsearchTestCase {
         assertThat("1.5gb", is(new ByteSizeValue((long) (1024 * 1.5), ByteSizeUnit.MB).toString()));
         assertThat("1536gb", is(new ByteSizeValue((long) (1024 * 1.5), ByteSizeUnit.GB).toString()));
     }
+
+    @Test
+    public void testParseCaseInsensitive() {
+        // Test lowercase
+        assertThat(ByteSizeValue.parseBytesSizeValue("12gb").bytes(), equalTo(12L * 1024 * 1024 * 1024));
+        assertThat(ByteSizeValue.parseBytesSizeValue("12mb").bytes(), equalTo(12L * 1024 * 1024));
+        assertThat(ByteSizeValue.parseBytesSizeValue("12kb").bytes(), equalTo(12L * 1024));
+        assertThat(ByteSizeValue.parseBytesSizeValue("12g").bytes(), equalTo(12L * 1024 * 1024 * 1024));
+        assertThat(ByteSizeValue.parseBytesSizeValue("12m").bytes(), equalTo(12L * 1024 * 1024));
+        assertThat(ByteSizeValue.parseBytesSizeValue("12k").bytes(), equalTo(12L * 1024));
+
+        // Test uppercase
+        assertThat(ByteSizeValue.parseBytesSizeValue("12GB").bytes(), equalTo(12L * 1024 * 1024 * 1024));
+        assertThat(ByteSizeValue.parseBytesSizeValue("12MB").bytes(), equalTo(12L * 1024 * 1024));
+        assertThat(ByteSizeValue.parseBytesSizeValue("12KB").bytes(), equalTo(12L * 1024));
+        assertThat(ByteSizeValue.parseBytesSizeValue("12G").bytes(), equalTo(12L * 1024 * 1024 * 1024));
+        assertThat(ByteSizeValue.parseBytesSizeValue("12M").bytes(), equalTo(12L * 1024 * 1024));
+        assertThat(ByteSizeValue.parseBytesSizeValue("12K").bytes(), equalTo(12L * 1024));
+
+        // Test mixed case
+        assertThat(ByteSizeValue.parseBytesSizeValue("12Gb").bytes(), equalTo(12L * 1024 * 1024 * 1024));
+        assertThat(ByteSizeValue.parseBytesSizeValue("12Mb").bytes(), equalTo(12L * 1024 * 1024));
+        assertThat(ByteSizeValue.parseBytesSizeValue("12Kb").bytes(), equalTo(12L * 1024));
+        assertThat(ByteSizeValue.parseBytesSizeValue("12gB").bytes(), equalTo(12L * 1024 * 1024 * 1024));
+        assertThat(ByteSizeValue.parseBytesSizeValue("12mB").bytes(), equalTo(12L * 1024 * 1024));
+        assertThat(ByteSizeValue.parseBytesSizeValue("12kB").bytes(), equalTo(12L * 1024));
+    }
 }
