@@ -112,4 +115,27 @@ public class CompletionFieldMapperTests extends ElasticsearchTestCase {
         assertThat(configMap.get("analyzer").toString(), is("simple"));
     }

+    @Test
+    public void testRejectsFloatWeights() throws Exception {
+        String mapping = jsonBuilder().startObject().startObject("type1")
+                .startObject("properties").startObject("completion")
+                .field("type", "completion")
+                .endObject().endObject()
+                .endObject().endObject().string();
+
+        DocumentMapper defaultMapper = MapperTestUtils.newParser().parse(mapping);
+        try {
+            defaultMapper.parse("type1", "1", jsonBuilder().startObject()
+                    .startObject("completion")
+                    .field("input", "suggestion")
+                    .field("weight", 1.5)
+                    .endObject()
+                    .endObject().bytes());
+            fail("Expected weight parsing to fail");
+        } catch (MapperParsingException e) {
+            assertThat(e.getCause(), instanceOf(ElasticSearchIllegalArgumentException.class));
+            assertThat(e.getCause().getMessage(), containsString("Weight must be an integer"));
+        }
+    }
+
 }
