+    /**
+     * Returns the current base path within the blob store. Repository implementations can use this path
+     * to create additional containers or resolve resources relative to the repository root.
+     */
+    protected final BlobPath basePath() {
+        return basePath;
+    }
+
+    /**
+     * Updates the base path used by this repository. This method is intended to be invoked by repository implementations
+     * during construction in order to customize the location within the underlying blob store where snapshot data is kept.
+     * The base path cannot be changed once the repository has been started.
+     *
+     * @param basePath new base path
+     */
+    protected final void setBasePath(BlobPath basePath) {
+        if (basePath == null) {
+            throw new ElasticSearchIllegalArgumentException("base path cannot be null");
+        }
+        if (lifecycle.started()) {
+            throw new ElasticSearchIllegalStateException("cannot set base path after repository has been started");
+        }
+        this.basePath = basePath;
+    }
