 package org.elasticsearch.index.mapper.completion;

+import org.elasticsearch.ElasticSearchIllegalArgumentException;
 import org.elasticsearch.common.xcontent.XContentBuilder;
 import org.elasticsearch.common.xcontent.json.JsonXContent;
 import org.elasticsearch.index.mapper.DocumentMapper;
@@ -32,6 +33,7 @@ import java.util.Map;

 import static org.elasticsearch.common.xcontent.XContentFactory.jsonBuilder;
 import static org.hamcrest.MatcherAssert.assertThat;
+import static org.hamcrest.Matchers.containsString;
 import static org.hamcrest.Matchers.instanceOf;
 import static org.hamcrest.Matchers.is;

@@ -112,4 +114,45 @@ public class CompletionFieldMapperTests extends ElasticsearchTestCase {
         assertThat(configMap.get("analyzer").toString(), is("simple"));
     }

+    @Test
+    public void testFloatWeightRejected() throws Exception {
+        String mapping = jsonBuilder().startObject().startObject("type1")
+                .startObject("properties").startObject("completion")
+                .field("type", "completion")
+                .endObject().endObject()
+                .endObject().endObject().string();
+
+        DocumentMapper defaultMapper = MapperTestUtils.newParser().parse(mapping);
+
+        // Test with float weight - should throw exception
+        String documentWithFloatWeight = jsonBuilder().startObject()
+                .startObject("completion")
+                .field("input", "test")
+                .field("weight", 1.5f)
+                .endObject()
+                .endObject().string();
+
+        try {
+            defaultMapper.parse("test", "type1", "1", documentWithFloatWeight.getBytes());
+            org.junit.Assert.fail("Expected ElasticSearchIllegalArgumentException for float weight");
+        } catch (ElasticSearchIllegalArgumentException e) {
+            assertThat(e.getMessage(), containsString("Weight must be an integer value, but was a floating-point number"));
+        }
+
+        // Test with double weight - should throw exception
+        String documentWithDoubleWeight = jsonBuilder().startObject()
+                .startObject("completion")
+                .field("input", "test")
+                .field("weight", 2.7d)
+                .endObject()
+                .endObject().string();
+
+        try {
+            defaultMapper.parse("test", "type1", "1", documentWithDoubleWeight.getBytes());
+            org.junit.Assert.fail("Expected ElasticSearchIllegalArgumentException for double weight");
+        } catch (ElasticSearchIllegalArgumentException e) {
+            assertThat(e.getMessage(), containsString("Weight must be an integer value, but was a floating-point number"));
+        }
+    }
+
 }
