+
+    private void ensureNodeIdExists(DiscoveryNodes nodes, String nodeId) {
+        if (!nodes.dataNodes().keys().contains(nodeId)) {
+            throw new ElasticSearchIllegalArgumentException("No data node with id[" + nodeId + "] found");
+        }
+    }
 }
